import CartActionTypes from './cart.types';
import { addItemToCart } from './card.utils';

const INTIAL_STATE = {
  hidden: true,
  cartItems: []
};

const cartReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case CartActionTypes.TOGGLE_CART_HIDEEN:
      return {
        ...state,
        hidden: !state.hidden
      };
    case CartActionTypes.ADD_ITEM:
      return {
        ...state,
        cartItems: addItemToCart(state.cardItems, action.payload)
      };
    default:
      return state;
  }
};

export default cartReducer;
