import styled from 'styled-components';

export const CartItemComponent = styled.div`
  width: 100%;
  display: flex;
  height: 80px;
  margin-bottom: 15px;
`;

export const CartItemImageComponent = styled.img`
  width: 30%;
`;

export const CartItemDetailsComponent = styled.div`
  width: 70%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  padding: 10px 20px;
`;
export const CartItemNameComponent = styled.span`
  font-size: 16px;
`;
export const CartItemPriceComponent = styled.span``;
